import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const Graph = ({ data }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const width = 500;
    const height = 300;
    const margin = { top: 20, right: 20, bottom: 40, left: 40 };

    // Set the domain for x and y scales to 100
    const xScale = d3.scaleLinear().domain([0, 100]).range([0, width]);
    const yScale = d3.scaleLinear().domain([0, 100]).range([height, 0]);

    // Create SVG container with margins
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // Create background rectangle with beige color
    svg.append('rect')
      .attr('width', width)
      .attr('height', height)
      .attr('fill', 'beige');

    // Create circles for each data point
    const circles = svg.selectAll('circle')
      .data(data)
      .enter()
      .append('circle')
      .attr('cx', d => xScale(d.x))
      .attr('cy', d => yScale(d.y))
      .attr('r', 5) // radius of the circle
      .attr('fill', 'blue'); // Set a specific color for the circles

    // Add event listeners for mouseover and mouseout
    circles
      .on('mouseover', handleMouseOver)
      .on('mouseout', handleMouseOut);

    // Add x-axis with custom ticks
    const xAxis = d3.axisBottom(xScale)
      .ticks(10) // Set the number of ticks
      .tickFormat(d3.format('.0f')); // Customize the format of the ticks
    svg.append('g')
      .attr('transform', `translate(0,${height})`)
      .call(xAxis);

    // Add x-axis label
    svg.append('text')
      .attr('text-anchor', 'middle')
      .attr('transform', `translate(${width / 2},${height + margin.top + 10})`)
      .text('X-Axis Label');

    // Add y-axis with custom ticks
    const yAxis = d3.axisLeft(yScale)
      .ticks(10) // Set the number of ticks
      .tickFormat(d3.format('.0f')); // Customize the format of the ticks
    svg.append('g')
      .call(yAxis);

    // Add y-axis label
    svg.append('text')
      .attr('text-anchor', 'middle')
      .attr('transform', 'rotate(-90)')
      .attr('y', -margin.left + 10)
      .attr('x', -height / 2)
      .text('Y-Axis Label');

    // Function to handle mouseover event
    function handleMouseOver(event, d) {
      // Show tooltip or update text element with x and y values
      svg.append('text')
        .attr('id', 'tooltip')
        .attr('x', xScale(d.x))
        .attr('y', yScale(d.y) - 10)
        .attr('text-anchor', 'middle')
        .text(`(${d.x}, ${d.y})`);
    }

    // Function to handle mouseout event
    function handleMouseOut() {
      // Remove tooltip or text element
      svg.select('#tooltip').remove();
    }
  }, [data]);

  return (
    <div className="w-full max-w-md mx-auto">
      <svg ref={svgRef}></svg>
    </div>
  );
};

export default Graph;
