import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const ScatterPlot = ({ data }) => {
  const svgRef = useRef(null);

  // Array to store randomly generated colors
  const colors = [];

  useEffect(() => {
    const width = 500;
    const height = 300;
    const margin = { top: 20, right: 20, bottom: 40, left: 40 };

    // Create scales
    const xScale = d3.scaleLinear().domain([0, d3.max(data, d => d.x)]).range([0, 1000]);
    const yScale = d3.scaleLinear().domain([0, d3.max(data, d => d.y)]).range([height, 1000]);

    // Create SVG container with margins
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`)
      .style('background-color', 'lightgray');

    // Create circles for each data point with random colors
    svg.selectAll('circle')
      .data(data)
      .enter()
      .append('circle')
      .attr('cx', d => xScale(d.x))
      .attr('cy', d => yScale(d.y))
      .attr('r', 5) // radius of the circle
      .attr('fill', (d, i) => getRandomColor(i)); // Pass index to getRandomColor

    // Store colors in the array
    svg.selectAll('circle').each((d, i) => {
      colors[i] = getRandomColor(i);
    });

    // Add x-axis
    const xAxis = d3.axisBottom(xScale);
    svg.append('g')
      .attr('transform', `translate(0,${height})`)
      .call(xAxis);

    // Add x-axis label
    svg.append('text')
      .attr('text-anchor', 'middle')
      .attr('transform', `translate(${width / 2},${height + margin.top + 10})`)
      .text('X-Axis Label');

    // Add y-axis
    const yAxis = d3.axisLeft(yScale);
    svg.append('g')
      .call(yAxis);

    // Add y-axis label
    svg.append('text')
      .attr('text-anchor', 'middle')
      .attr('transform', 'rotate(-90)')
      .attr('y', -margin.left + 10)
      .attr('x', -height / 2)
      .text('Y-Axis Label');
  }, [data]);

  // Function to generate a random color
  const getRandomColor = index => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    // Update the color in the array
    colors[index] = color;
    return color;
  };

  return (
    <div className="w-full max-w-md mx-auto">
      <svg ref={svgRef}></svg>
    </div>
  );
};

export default ScatterPlot;
