    e.preventDefault();
    if (songTitle && artistName) {
      onAddSong({ title: songTitle, artist: artistName });
      // Clear the input fields after adding a song
      setSongTitle('');
      setArtistName('');
    }
  };

  return (
    <div className='flex text-gotham justify-around p-2'>
        <form>
          <label>
            <input 
